import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";

const Home = () => {
  const [menuCategories, setMenuCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/menu")
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch menu");
        return res.json();
      })
      .then((data) => {
        setMenuCategories(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const handleAddToList = (item) => {
    alert(`Bạn đã thêm "${item.name}" vào danh sách!`);
  };

  if (loading) return <p style={{ textAlign: "center", marginTop: "3rem" }}>Đang tải dữ liệu...</p>;
  if (error) return <p style={{ color: "red", textAlign: "center" }}>Lỗi: {error}</p>;

  return (
    <>
      <Header />
      <div
        style={{
          padding: "2rem",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
          minHeight: "80vh",
          backgroundColor: "#fdfdfd",
        }}
      >
        <h1 style={{ textAlign: "center", color: "#d62828", fontSize: "2.5rem", marginBottom: "3rem" }}>
          Thực Đơn Nhà Hàng Hải Sản Việt
        </h1>

        {menuCategories.map((category) => (
          <section key={category._id} style={{ marginBottom: "4rem" }}>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: "1rem",
              }}
            >
              <div>
                <h2 style={{ color: "#003049", fontSize: "1.5rem" }}>{category.name}</h2>
                <p style={{ fontStyle: "italic", color: "#6c757d" }}>{category.description}</p>
              </div>
              <button
                style={{
                  backgroundColor: "#e76f51",
                  color: "white",
                  border: "none",
                  borderRadius: "5px",
                  padding: "0.5rem 1rem",
                  cursor: "pointer",
                  fontWeight: "bold",
                }}
                onClick={() => alert(`Xem thêm món ăn trong: ${category.name}`)}
              >
                Xem thêm
              </button>
            </div>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
                gap: "1.5rem",
              }}
            >
              {category.items
                ?.filter((item) => item.isAvailable)
                .slice(0, 6)
                .map((item) => (
                  <div
                    key={item._id}
                    style={{
                      border: "1px solid #eee",
                      borderRadius: "10px",
                      backgroundColor: "#fff",
                      boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
                      padding: "1rem",
                      transition: "transform 0.3s",
                    }}
                    onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.03)")}
                    onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
                  >
                    <img
                      src={item.image}
                      alt={item.name}
                      style={{
                        width: "100%",
                        height: "150px",
                        objectFit: "cover",
                        borderRadius: "6px",
                        marginBottom: "0.75rem",
                      }}
                      onError={(e) =>
                        (e.currentTarget.src =
                          "https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=800&q=80")
                      }
                    />
                    <h3 style={{ margin: "0.5rem 0", color: "#264653" }}>{item.name}</h3>
                    <p style={{ fontSize: "0.9rem", color: "#555", minHeight: "3em" }}>{item.description}</p>
                    <p style={{ fontWeight: "bold", color: "#2a9d8f" }}>
                      {item.price.toLocaleString("vi-VN", {
                        style: "currency",
                        currency: "VND",
                      })}
                    </p>
                    <button
                      style={{
                        marginTop: "0.75rem",
                        padding: "0.5rem",
                        borderRadius: "4px",
                        border: "none",
                        backgroundColor: "#2a9d8f",
                        color: "#fff",
                        cursor: "pointer",
                        width: "100%",
                      }}
                      onClick={() => handleAddToList(item)}
                    >
                      Thêm vào danh sách
                    </button>
                  </div>
                ))}
            </div>
          </section>
        ))}
      </div>
      <Footer />
    </>
  );
};

export default Home;
