// src/pages/Menu.jsx
import React, { useEffect, useState } from "react";

const Menu = () => {
  const [menuCategories, setMenuCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/menu")
      .then((res) => {
        if (!res.ok) throw new Error("Không thể tải thực đơn");
        return res.json();
      })
      .then((data) => {
        setMenuCategories(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p style={{ textAlign: "center" }}>Đang tải thực đơn...</p>;
  if (error) return <p style={{ color: "red", textAlign: "center" }}>Lỗi: {error}</p>;

  return (
    <div style={{ padding: "2rem", fontFamily: "Segoe UI" }}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem", color: "#007acc" }}>
        Thực Đơn Nhà Hàng
      </h1>
      {menuCategories.map((category) => (
        <section key={category._id} style={{ marginBottom: "4rem" }}>
          <h2 style={{ borderBottom: "2px solid #007acc", paddingBottom: "0.5rem", color: "#004080" }}>
            {category.name}
          </h2>
          <p style={{ fontStyle: "italic", color: "#666" }}>{category.description}</p>

          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
              gap: "1.5rem",
              marginTop: "1rem",
            }}
          >
            {category.items
              ?.filter((item) => item.isAvailable)
              .map((item) => (
                <div
                  key={item._id}
                  style={{
                    border: "1px solid #ddd",
                    borderRadius: "8px",
                    padding: "1rem",
                    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                    backgroundColor: "#fff",
                    transition: "transform 0.2s",
                  }}
                  onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
                  onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
                >
                  <img
                    src={item.image}
                    alt={item.name}
                    onError={(e) => {
                      e.currentTarget.src =
                        "https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=800&q=80";
                    }}
                    style={{
                      width: "100%",
                      height: "150px",
                      objectFit: "cover",
                      borderRadius: "6px",
                      marginBottom: "0.75rem",
                      backgroundColor: "#f0f0f0",
                    }}
                  />
                  <h3 style={{ margin: "0.5rem 0", color: "#222" }}>{item.name}</h3>
                  <p style={{ fontSize: "0.9rem", color: "#555", minHeight: "3em" }}>
                    {item.description}
                  </p>
                  <p style={{ fontWeight: "bold", marginTop: "0.5rem", color: "#007acc" }}>
                    {item.price.toLocaleString()} VND
                  </p>
                </div>
              ))}
          </div>
        </section>
      ))}
    </div>
  );
};

export default Menu;
