import React, { useEffect, useState } from "react";

const Home = () => {
  const [menuCategories, setMenuCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/menu")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to fetch menu");
        }
        return res.json();
      })
      .then((data) => {
        setMenuCategories(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Đang tải dữ liệu...</p>;
  if (error) return <p>Lỗi: {error}</p>;

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Menu Nhà Hàng Hải Sản Việt Nam</h1>
      {menuCategories.map((category) => (
        <section key={category._id} style={{ marginBottom: "3rem" }}>
          <h2>{category.name}</h2>
          <p>{category.description}</p>
          <div style={{ display: "flex", flexWrap: "wrap", gap: "1rem" }}>
          {category.items?.filter((item) => item.isAvailable).map((item) => (
                <div
                  key={item._id}
                  style={{
                    border: "1px solid #ddd",
                    borderRadius: "8px",
                    padding: "1rem",
                    width: "220px",
                    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
                  }}
                >
                  <img
                    src={item.image}
                    alt={item.name}
                    style={{ width: "100%", height: "140px", objectFit: "cover", borderRadius: "4px" }}
                  />
                  <h3 style={{ margin: "0.5rem 0" }}>{item.name}</h3>
                  <p style={{ fontSize: "0.9rem", color: "#555" }}>{item.description}</p>
                  <p style={{ fontWeight: "bold", marginTop: "0.5rem" }}>
                    {item.price.toLocaleString()} VND
                  </p>
                </div>
              ))}
          </div>
        </section>
      ))}
    </div>
  );
};

export default Home;
