import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";

const Home = () => {
  const [menuCategories, setMenuCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/menu")
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch menu");
        return res.json();
      })
      .then((data) => {
        setMenuCategories(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const handleAddToList = (item) => {
    alert(`Bạn đã thêm "${item.name}" vào danh sách!`);
  };

  const handleViewMore = (categoryName) => {
    alert(`Xem thêm món ăn trong: ${categoryName}`);
    // Hoặc bạn có thể redirect tới trang chi tiết category ở đây
  };

  if (loading)
    return (
      <p style={{ textAlign: "center", marginTop: "3rem" }}>Đang tải dữ liệu...</p>
    );
  if (error)
    return (
      <p style={{ color: "red", textAlign: "center" }}>Lỗi: {error}</p>
    );

  return (
    <>
      <Header />

      {/* Banner */}
      <div
        style={{
          position: "relative",
          width: "100%",
          height: "320px",
          backgroundImage:
            "url('https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=1400&q=80')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "white",
          fontSize: "3rem",
          fontWeight: "bold",
          textShadow: "2px 2px 6px rgba(0,0,0,0.7)",
        }}
      >
        Nhà Hàng Hải Sản Việt Nam
      </div>

      {/* Chào mừng */}
      <section
        style={{
          padding: "3rem 1.5rem",
          maxWidth: "900px",
          margin: "auto",
          textAlign: "center",
          fontSize: "1.25rem",
          color: "#264653",
        }}
      >
        <p>
          Chào mừng bạn đến với Nhà Hàng Hải Sản Việt Nam – nơi tinh hoa hải sản
          tươi ngon, chất lượng phục vụ tận tâm cùng không gian ấm cúng, thân
          thiện. Hãy khám phá thực đơn đa dạng và trải nghiệm những món ăn đậm
          đà hương vị Việt ngay hôm nay!
        </p>
      </section>

      {/* Thực đơn */}
      <main
        style={{
          padding: "2rem 1rem 4rem",
          maxWidth: "1100px",
          margin: "auto",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
          backgroundColor: "#fefefe",
          borderRadius: "8px",
          boxShadow: "0 8px 20px rgb(0 0 0 / 0.1)",
        }}
      >
        <h2
          style={{
            textAlign: "center",
            fontSize: "2rem",
            color: "#d62828",
            marginBottom: "3rem",
            fontWeight: "700",
          }}
        >
          Thực Đơn Nổi Bật
        </h2>

        {menuCategories.map((category) => (
          <section key={category._id} style={{ marginBottom: "4rem" }}>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: "1rem",
              }}
            >
              <div>
                <h3
                  style={{
                    color: "#003049",
                    fontSize: "1.5rem",
                    margin: 0,
                    fontWeight: "600",
                  }}
                >
                  {category.name}
                </h3>
                <p
                  style={{
                    fontStyle: "italic",
                    color: "#6c757d",
                    marginTop: "0.25rem",
                  }}
                >
                  {category.description}
                </p>
              </div>
              <button
                onClick={() => handleViewMore(category.name)}
                style={{
                  backgroundColor: "#e76f51",
                  color: "white",
                  border: "none",
                  borderRadius: "5px",
                  padding: "0.5rem 1rem",
                  cursor: "pointer",
                  fontWeight: "bold",
                  transition: "background-color 0.3s",
                }}
                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#d65a3b")}
                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "#e76f51")}
              >
                Xem thêm
              </button>
            </div>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
                gap: "1.5rem",
              }}
            >
              {category.items
                ?.filter((item) => item.isAvailable)
                .slice(0, 6)
                .map((item) => (
                  <article
                    key={item._id}
                    style={{
                      border: "1px solid #eee",
                      borderRadius: "10px",
                      backgroundColor: "#fff",
                      boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
                      padding: "1rem",
                      transition: "transform 0.3s",
                      cursor: "pointer",
                    }}
                    onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.03)")}
                    onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
                  >
                    <img
                      src={item.image}
                      alt={item.name}
                      style={{
                        width: "100%",
                        height: "150px",
                        objectFit: "cover",
                        borderRadius: "6px",
                        marginBottom: "0.75rem",
                        backgroundColor: "#f8f8f8",
                      }}
                      onError={(e) =>
                        (e.currentTarget.src =
                          "https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=800&q=80")
                      }
                    />
                    <h4 style={{ margin: "0.5rem 0", color: "#264653" }}>{item.name}</h4>
                    <p
                      style={{
                        fontSize: "0.9rem",
                        color: "#555",
                        minHeight: "3em",
                        marginBottom: "0.5rem",
                      }}
                    >
                      {item.description}
                    </p>
                    <p
                      style={{
                        fontWeight: "bold",
                        color: "#2a9d8f",
                        marginBottom: "0.75rem",
                      }}
                    >
                      {item.price.toLocaleString("vi-VN", {
                        style: "currency",
                        currency: "VND",
                      })}
                    </p>
                    <button
                      onClick={() => handleAddToList(item)}
                      style={{
                        width: "100%",
                        padding: "0.5rem",
                        borderRadius: "5px",
                        border: "none",
                        backgroundColor: "#2a9d8f",
                        color: "white",
                        fontWeight: "600",
                        cursor: "pointer",
                        transition: "background-color 0.3s",
                      }}
                      onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#1f6f7a")}
                      onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "#2a9d8f")}
                    >
                      Thêm vào danh sách
                    </button>
                  </article>
                ))}
            </div>
          </section>
        ))}
      </main>

      <Footer />
    </>
  );
};

export default Home;
